import React, { Component } from 'react'

export default class AddTodo extends Component {
	render() {
		return (
			<form>
				<input
					type='text'
					name='title'
					style={{
						flex: '10',
						padding: '5px'
					}}
					placeholder='Add a new todo item ...'
				/>
				<input 
					type='submit'
					value='Add'
					className='btn'
					style={{
						flex: '1'
					}}
				/>	
			</form>
			)
		}
	}
